target.groovy:
if (System.getenv("CLASSPATH")) {
   ant.fail(message:"Unset the CLASSPATH. If you are a SAFS user, you likely have Groovy on the CLASSPATH.\n" +
                    "When Groovy is loaded by Ant's root classloader it causes problems.\n" +
                    "Groovy must be loaded with the other jars such as Selenium.")
}
ant.typedef(name:'groovyc', classname:'org.codehaus.groovy.ant.Groovyc', classpath:'${groovy.classpath}')

  ant.mkdir(dir:'${test.core.classes}')
  //We don't need to create source directory, it should exist already!
  //ant.mkdir(dir:'${safs.test.core.source}/groovy')

  ant.groovyc(
    srcdir:'${safs.test.core.source}/groovy',
    destdir:'${test.core.classes}',
    classpath:'${test.core.compile.classpath}',
    encoding:'UTF-8',
  ) {
	//compile java source with UTF-8 encoding
    javac(debug:'on', encoding:'UTF-8')
  }

sequential.groovy:
  ant.echo "'safs.test.core' is running ... "
  // Clean the report directories.
  ant.delete(dir:'${test.core.reports}/xml', failonerror:false)
  ant.delete(dir:'${test.core.reports}/html', failonerror:false)
  ant.mkdir(dir:'${test.core.reports}/xml')

  // Run the tests.
  def debugPort = properties.'test.core.debug.port'  // set to non-zero to attach to a debugger.
  def safsDebugPortProperty = 'org.safs.test.forked.jvm.debug.port'
  def safsDebugPort = properties.get(safsDebugPortProperty)
  ant.property(name:'test.core.timeout', value:'900000') // 15 min default
  ant.property(name:'seleniumdist.location', location:'${seleniumdist}')

  //ant.echo(message:'test.core.compile.classpath=${test.core.compile.classpath}')
  //ant.echo(message:'test.core.classes=${test.core.classes}')

  ant.junit(
    failureProperty:"tests-failed",
    fork:true,
    printsummary:true,
    timeout:'${test.core.timeout}',
    dir:'${basedir}',
  ) {
    classpath {
       path(refid:"test.core.compile.classpath")
       pathelement(location:'${test.core.classes}')
    }
    env(key:'SELENIUM_PLUS', value:'${seleniumdist.location}')
	env(key:'PRODUCT_NAME', value:'SeleniumPlus')
    if (debugPort) {
      jvmarg(value:"-Xdebug")
      jvmarg(value:"-Xrunjdwp:transport=dt_socket,address=$debugPort,suspend=y,server=y")
    }
    if (safsDebugPort) {
      sysproperty(key:safsDebugPortProperty, value:safsDebugPort)
    }
    formatter(type:"xml")
    batchtest(todir:'${test.core.reports}/xml') {
      fileset(dir:'${test.core.classes}') {
        include(name:"**/*.class")
        exclude(name:'**/*$$*.class')
        exclude(name:'**/LogFileUtil.class')
        exclude(name:'**/SeleniumPlusTestUtil.class')
        exclude(name:'**/TestUtil.class')
        exclude(name:'**/AppMapBuilder.class')
        exclude(name:'**/WebappServer.class')
      }
    }
  }
  // run the junit report
  ant.mkdir(dir:'${test.core.reports}/html')
  ant.junitreport(todir:'${test.core.reports}/html') {
    fileset(dir:'${test.core.reports}/xml') {
      include(name:'TEST-*.xml')
    }
    report(format:'frames', todir:'${test.core.reports}/html')
  }
  // if there were test failures, fail the build.
  if (properties.'tests-failed') {
  ant.fail(message:'One or more tests have failed.  See ${test.core.reports}/html/index.html for details.')
}

// use a var temporarily to get the value of @{fileToHash} into a groovy reference.
ant.var(name:'fileToHash', value:'@{fileToHash}')
def fileToHash = properties.'fileToHash'
ant.var(name:'fileToHash', unset:true)

def file = new File(fileToHash).absoluteFile
def parentFile = file.parentFile

// get the path to the commons-codec jar
def path = ant.path(id:'commonsCodecJarPath') {
  fileset(dir:"buildLib") {
    include(name:'**/commons-codec*.jar')
  }
}

// create a classloader and add the path to the commons-codec jar
def groovyClassLoader = new GroovyClassLoader(this.class.classLoader)
groovyClassLoader.addClasspath(path.toString())

// call DigestUtils.md5Hex() to get the hash for the file.
def clazz = groovyClassLoader.loadClass('org.apache.commons.codec.digest.DigestUtils')
def method = clazz.getMethod("md5Hex", [InputStream.class] as Class[])

def fis = new FileInputStream(file)
def hash = method.invoke(null, fis)
fis.close()

// write the hash to a file with .md5 extension.
def hashFile = new File(parentFile, file.name + ".md5")
hashFile.write(hash)

// use a var temporarily to get the value of @{fileToHash} into a groovy reference.
ant.var(name:'file.temp', value:'@{file}')
def file = new File(properties.'file.temp').absoluteFile
ant.var(name:'file.temp', value:'@{tofile}')
def tofile = new File(properties.'file.temp').absoluteFile
ant.var(name:'file.temp', unset:true)

def destDir = tofile.parentFile
destDir.mkdirs()

ant.copy(file:file, tofile:tofile, preservelastmodified:true)

def hashFile = new File(file.parentFile, file.name + ".md5")
if (hashFile.exists()) {
  def hashFileDest = new File(destDir, tofile.name + ".md5")
  ant.copy(file:hashFile, tofile:hashFileDest, preservelastmodified:true)
}
